mapper.select:
SELECT 

FROM jf_xl_zg a

WHERE a.id = #{id}

SELECT 

FROM jf_xl_zg a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.zgjf = #{zgjf.id}


		AND a.zgjf in (SELECT a.Id FROM jf_xx a WHERE a.name like concat('%',#{jfName},'%'))


		AND a.kzzd2 = #{kzzd2}


		AND a.kzzd4 = #{kzzd4}


		AND a.zgdh LIKE 
			'%'||#{zgdh}||'%'
			'%'+#{zgdh}+'%'
			concat('%',#{zgdh},'%')


		AND
		DATE_FORMAT(a.zgrq, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')


		AND DATE_FORMAT(#{overDate}, '%Y-%m-%d') >= DATE_FORMAT(a.zgrq, '%Y-%m-%d')

	${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC



	limit ${limit},10

SELECT 

FROM jf_xl_zg a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE jf_xl_zg SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
