mapper.select:
SELECT 

FROM jf_zg a

WHERE a.id = #{id}

SELECT
FROM jf_zg a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.zgjf = #{zgjf.id}
AND a.zgdh LIKE '%'||#{zgdh}||'%' '%'+#{zgdh}+'%' concat('%',#{zgdh},'%')
AND DATE_FORMAT(a.zgrq, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
AND DATE_FORMAT(#{overDate}, '%Y-%m-%d') >= DATE_FORMAT(a.zgrq, '%Y-%m-%d')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
limit ${limit},10

SELECT 

FROM jf_zg a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE jf_zg SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
